$(document).ready(function(){$(".anchor-wel").click(function(){$("html,body").animate({scrollTop:$("#welcome").offset().top},260)});$(".anchor-abt").click(function(){$("html,body").animate({scrollTop:$("#about").offset().top},260)});$(".anchor-pro").click(function(){$("html,body").animate({scrollTop:$("#project").offset().top},260)});$(".anchor-con").click(function(){$("html,body").animate({scrollTop:$("#contact").offset().top},260)});$("#nav a").click(function(){$("#nav a").removeClass("bg");$(this).addClass("bg")})});$(function(){$(window).scroll(function(){var a=$(window).scrollTop()+350;for(i=1;i<5;i++){if($(".a"+i).offset().top<=a){$("#nav a").removeClass("bg");$(".a"+i+i).addClass("bg")}}})});$(".btn-bar").click(function(){$("#nav").slideToggle()});$("#nav li a").click(function(b){$(".ripple").remove();var h=$(this).offset().left,d=$(this).offset().top,g=$(this).width(),c=$(this).height();$(this).prepend("<span class='ripple'></span>");if(g>=c){c=g}else{g=c}var a=b.pageX-h-g/2;var f=b.pageY-d-c/2;$(".ripple").css({width:g,height:c,top:f+"px",left:a+"px"}).addClass("rippleEffect")});